    int contador = 0;
    TBEjercicio1 tbl = new TBEjercicio1();

    //Verificar número máximo
    public int comprobadorNumeroMaximo(Integer[] numeros) {
        int numeroMaximo = numeros[0];
        for (int i = 0; i < numeros.length; i++) {
            if (numeros[i] > numeroMaximo) {
                numeroMaximo = numeros[i];
            }
        }
        return numeroMaximo;
    }

    // Verificar número mínimo
    public int comprobadorNumeroMinimo(Integer[] numeros) {
        int numeroMinimo = numeros[0];
        for (int i = 0; i < numeros.length; i++) {
            if (numeros[i] < numeroMinimo) {
                numeroMinimo = numeros[i];
            }
        }
        return numeroMinimo;
    }
    
    private void txtNumerosIngresadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtNumerosIngresadosActionPerformed
String inputText = txtNumerosIngresados.getText().trim();
    if (!inputText.isEmpty()) {
        try {
            // Intenta convertir el texto a un número entero
            int numeroActual = Integer.parseInt(inputText);

            // Verifica si aún se pueden ingresar más números
            if (contador < 10) {
                tbl.numeros[contador++] = numeroActual;
                txtNumerosIngresados.setText("");
                // Muestra el número en el JTextArea
                txtAMostrarNumeros.append(numeroActual + ", ");
            } else {
                JOptionPane.showMessageDialog(rootPane, "Ya ingresó 10 números");
                for (int i = 0; i < tbl.numeros.length; i++) {
                    System.out.println(tbl.numeros[i]);
                }
            }

        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(rootPane, "Ingrese un número válido");
            txtNumerosIngresados.setText(null);
        }
    } else {
        JOptionPane.showMessageDialog(rootPane, "Ingrese un número");
    }                                 
    }//GEN-LAST:event_txtNumerosIngresadosActionPerformed

    private void btnVerTablaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVerTablaActionPerformed
        tbl.numMaximo = comprobadorNumeroMaximo(tbl.numeros);
        tbl.numMinimo = comprobadorNumeroMinimo(tbl.numeros);
        Menu.escritorio.add(tbl);
        tbl.setVisible(true);
    }//GEN-LAST:event_btnVerTablaActionPerformed

    private void btnNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNuevoActionPerformed
        txtAMostrarNumeros.setText(null);
        txtNumerosIngresados.setText(null);
    }//GEN-LAST:event_btnNuevoActionPerformed

    
    
    
