    private void btnBuscarPalabraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarPalabraActionPerformed
        String buscar = txtBuscarPalabra.getText();
        int contador = 0;

        DefaultTableModel modelo = (DefaultTableModel) tblPalabras.getModel();
        for (int i = 0; i < modelo.getRowCount(); i++) {
            String palabraTabla = modelo.getValueAt(i, 0).toString();
            if (buscar.equals(palabraTabla)) {
                contador++;
            }
        }

        if (contador > 0) {
            JOptionPane.showMessageDialog(this, "La palabra '" + buscar + "' se repite " + contador + " veces.", "Mensaje", JOptionPane.INFORMATION_MESSAGE);
        } else {
            JOptionPane.showMessageDialog(this, "La palabra '" + buscar + "' no se encuentra en la tabla.", "Mensaje", JOptionPane.INFORMATION_MESSAGE);
        }
        txtBuscarPalabra.setText(null);
    }//GEN-LAST:event_btnBuscarPalabraActionPerformed

    private void btnNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNuevoActionPerformed
        DefaultTableModel modelo = (DefaultTableModel) tblPalabras.getModel();
        modelo.setRowCount(0);
        txtDimension.setText(null);
        txtPalabraIngresada.setText(null);
    }//GEN-LAST:event_btnNuevoActionPerformed

    String palabrasAcumuladas = "";
    private void btnAgregarPalabraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarPalabraActionPerformed
        DefaultTableModel modelo = (DefaultTableModel) tblPalabras.getModel();
        String palabra = txtPalabraIngresada.getText();

        try {
            int tamanio = Integer.parseInt(txtDimension.getText());

            if (tamanio > 0) {
                palabrasAcumuladas += palabra + ",";

                if (palabrasAcumuladas.split(",").length == tamanio) {
                    JOptionPane.showMessageDialog(this, "La lista está llena", "Mensaje", JOptionPane.INFORMATION_MESSAGE);

                    String[] palabrasArray = palabrasAcumuladas.split(",");
                    for (String palabraActual : palabrasArray) {
                        modelo.addRow(new Object[]{palabraActual});
                    }

                    palabrasAcumuladas = "";
                    txtDimension.setText(null);
                    txtPalabraIngresada.setText(null);
                } else {
                    JOptionPane.showMessageDialog(this, "Agregada palabra: " + palabra, "Mensaje", JOptionPane.INFORMATION_MESSAGE);
                }
            } else {
                JOptionPane.showMessageDialog(this, "El tamaño de la lista debe ser mayor que 0", "Error", JOptionPane.ERROR_MESSAGE);
            }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Ingrese un número válido para el tamaño", "Error", JOptionPane.ERROR_MESSAGE);
        }
        txtPalabraIngresada.setText(null);

    }//GEN-LAST:event_btnAgregarPalabraActionPerformed

