    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        int tamanio = Integer.parseInt(txtLongitud.getText());
        String oracion = txtOracion.getText();
        String cadena = "";

        String[] lista = new String[tamanio];
        String[] palabras = oracion.split(" ");

        if (palabras.length == 2) {
            String palabra1 = palabras[0];
            String palabra2 = palabras[1];

            for (int i = 0; i < lista.length; i++) {
                
                if (i % 2 == 0) {
                    lista[i] = palabra2;
                } else {
                    lista[i] = palabra1;
                }

                cadena += lista[i] + "\n";
            }

            txtSalida.setText(cadena);
        } else {
            System.out.println("Se necesitan solo 2 palabras en la oraciÃ³n.");
        }


        txtOracion.setText("");
        txtLongitud.setText("");

    }//GEN-LAST:event_btnEnviarActionPerformed
    
